import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import WeatherWidget from "./WeatherWidget";
import { makeStyles } from "@material-ui/core/styles";
import { Container } from "@material-ui/core";

const useStyles = makeStyles({
  title: {
    color: "red",
  },
  location: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  widgetContainer: {
    fontSize: 14,
  },
});

export default function Weather() {
  const classes = useStyles();
  const [weather, setWeather] = useState({});
  const [error, setError] = useState(null);
  const mockAPI = require("./mockapi.json");
  useEffect(() => {
    const geo = navigator.geolocation;
    if (!geo) {
      setError("Geolocation is not supported");
      return;
    }
    const watcher = geo.watchPosition(onChange, onError);
    return () => geo.clearWatch(watcher);
  }, []);

  const onChange = ({ coords }) => {
    if (coords) {
      const lat = coords.latitude;
      const lon = coords.longitude;

      // We should load this from ENV variable
      const API_KEY = "f513eb8ca9bc54afc9773da7aeaaa41f";

      const apiUrl = `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${API_KEY}`;

      fetch(apiUrl)
        .then((response) => response.json())
        .then((data) => {
          if (data.cod == 200) setWeather(data);
          else setWeather(mockAPI);
        });
    }
  };

  const onError = (error) => {
    setError(error.message);
  };

  return (
    <Container maxWidth="sm">
      <AppBar position="static">
        <Toolbar>
          <IconButton
            edge="start"
            className={classes.menuButton}
            color="inherit"
            aria-label="menu"
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" className={classes.title}>
            News
          </Typography>
          <Button color="inherit">Login</Button>
        </Toolbar>
      </AppBar>
      <div className={classes.title}>Weather App</div>
      <div className={classes.location}>
        Location: {error ? error : weather.name || "Fetching..."}
      </div>
      <div className={classes.widgetContainer}>
        {weather.name && <WeatherWidget data={weather} />}
      </div>
    </Container>
  );
}
